import React, { useState } from "react";
import "../styles/Donate.css";

const Donate = () => {
  const [foodDetails, setFoodDetails] = useState({
    quantity: "",
    peopleServed: "",
    restaurantName: "",
    address: "",
  });

  const handleChange = (e) => {
    setFoodDetails({ ...foodDetails, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Food Donation Details:", foodDetails);
    alert("Food Donation Submitted Successfully!");
  };

  return (
    <div className="donate-page">
      <div className="form-container">
        <h2>Food Donation Form</h2>
        <form onSubmit={handleSubmit}>
          <input type="number" name="quantity" placeholder="Food Quantity (in Kg)" onChange={handleChange} required />
          <input type="number" name="peopleServed" placeholder="Number of People Served" onChange={handleChange} required />
          <input type="text" name="restaurantName" placeholder="Restaurant Name" onChange={handleChange} required />
          <textarea name="address" placeholder="Restaurant Address" onChange={handleChange} required />
          <button type="submit" className="btn">Submit Donation</button>
        </form>
      </div>
    </div>
  );
};

export default Donate;
