import React, { useState } from "react";
import "../styles/Profile.css";
import profilePic from "../assets/profile.jpeg"; // Replace with your own profile image

const Profile = () => {
  const [donationCount] = useState(12); // You can dynamically load this later

  return (
    <div className="profile-page">
      <h2>Your Profile</h2>

      <div className="profile-container">
        <div className="profile-image">
          <img src={profilePic} alt="User Profile" />
        </div>
        <div className="profile-details">
          <h3>Poojitha</h3>
          <p><strong>Email:</strong> poojitha@example.com</p>
          <p><strong>Phone:</strong> +91 9876543210</p>
          <p><strong>Location:</strong> Hyderabad</p>
          <button className="edit-btn">Edit Profile</button>
        </div>
      </div>

      <div className="donation-box">
        <h4>Total Donations</h4>
        <p>{donationCount}</p>
      </div>
    </div>
  );
};

export default Profile;
